mapper.insert:
insert into t_diagnosticbz_mapping_opt (ID ,DIAGNOSIS_CODE,DIAGNOSIS_NAME,BZDIAGNOSIS_CODE,BZDIAGNOSIS_NAME,REMARK,CREATE_TIME,CREATE_USER,UPDATE_TIME,UPDATE_USER,opt_type)
select ID ,DIAGNOSIS_CODE,DIAGNOSIS_NAME,BZDIAGNOSIS_CODE,BZDIAGNOSIS_NAME,REMARK,sysdate,#{loginName},sysdate,#{loginName},#{optType} from t_diagnosticbz_mapping
where id=#{id}

mapper.update:
begin
insert into t_diagnosticbz_mapping_opt (id,diagnosis_code,diagnosis_name,bzdiagnosis_code,bzdiagnosis_name,remark,opt_type,create_time,create_user,update_time,update_user) select id,diagnosis_code,diagnosis_name,bzdiagnosis_code,bzdiagnosis_name,remark,'导入更新' opt_type, sysdate create_time,#{loginName} create_user,sysdate update_time,#{loginName} update_user from t_diagnosticbz_mapping where diagnosis_code =#{option.field0};
insert into t_diagnosticbz_mapping_opt(id,diagnosis_code,diagnosis_name,bzdiagnosis_code,bzdiagnosis_name,remark,opt_type,create_time,create_user,update_time,update_user) select id,diagnosis_code,diagnosis_name,bzdiagnosis_code,bzdiagnosis_name,remark,'导入新增' opt_type,sysdate create_time,#{loginName} create_user,sysdate update_time,#{loginName} update_user from (select #{option.field5} id, #{option.field0} diagnosis_code,#{option.field1} diagnosis_name,#{option.field2} bzdiagnosis_code,#{option.field3} bzdiagnosis_name, #{option.field4} remark from dual) t where not exists (select 1 from t_diagnosticbz_mapping t1 where t1.diagnosis_code=t.diagnosis_code);
merge into t_diagnosticbz_mapping t1 using (select #{option.field0} diagnosis_code,#{option.field1} diagnosis_name,#{option.field2} bzdiagnosis_code,#{option.field3} bzdiagnosis_name, #{option.field4} remark from dual) t2 on (t1.diagnosis_code=t2.diagnosis_code) WHEN MATCHED THEN update set t1.diagnosis_name=t2.diagnosis_name,t1.bzdiagnosis_code=t2.bzdiagnosis_code,t1.bzdiagnosis_name=t2.bzdiagnosis_name,t1.remark=t2.remark, t1.update_time=sysdate,t1.update_user=#{loginName} WHEN NOT MATCHED THEN insert (id,diagnosis_code,diagnosis_name,bzdiagnosis_code,bzdiagnosis_name,remark,create_time,create_user,update_time,update_user) VALUES (#{option.field5},#{option.field0},#{option.field1},#{option.field2},#{option.field3},#{option.field4},sysdate,#{loginName},sysdate,#{loginName});
end;
